INSERT INTO plants (id, name, image_url, scientific_name, description, water_needs, light, humidity, temperature, winter_temperature, color_feature, plant_type, has_thorns, leaf_shape1, leaf_shape2, leaf_shape3, leaf_shape4, experience_level, pet_safety, size, difficulty, price_range, care_instructions, category, created_at) VALUES
(928, '스킨답서스 화이트 마블퀸 (마블퀸 스킨답서스)', NULL, 'Epipremnum aureum ''Marble Queen''', '스킨답서스 화이트 마블퀸은 흰 바탕에 초록빛 유화물감을 칠한 것 같은 아름다운 산반무늬 잎을 가진 덩굴성 식물입니다. 스킨답서스답게 키우기 까다롭지는 않지만, 잎에 흰 지분이 많은만큼 밝은 빛을 충분히 받을 수 있는 곳에서 키워주셔야 아름다운 무늬를 감상할 수 있어요. 주변 공기를 너무 건조하지 않게 키워주시면 금세 줄기를 길게 늘어뜨려 아름다운 모습으로 변할 거에요. 스킨답서스 화이트 마블퀸은 일산화탄소 제거능력이 뛰어난 공기정화 식물이지만 식물에 독성이 있어 아이들과 반려동물이 먹으면 위험하니 섭취하지 않도록 주의해주세요.', '평균 주 1~2회
흙 표면부터 3cm까지 마르면, 듬뿍 주세요', '반양지', '40~70%', '21~25℃', '13℃ 이상', '흰바탕에 초록무늬', '잎을 감상하는', false, '둥근모양', '길쭉한', '무늬가 있는', '잎 끝이 뾰족한', NULL, '주의 필요', '소형', '중', '7천원~1만5천원', NULL, NULL, '2025-05-26 15:33:25.38884'),
(952, '칼라데아 마코야나', NULL, 'Goeppertia makoyana', '칼라데아 마코야나는 진한 녹색의 반점이 있는 연녹색 잎을 가진 식물이에요. 붉은 빛을 띠는 잎 뒷면에도 선명한 무늬가 같이 보여 어디에서든 아름다운 무늬를 볼 수 있는 마란타과 식물입니다. 브라질의 열대우림에서 온 마코야나는 빛이 적은 곳에서도 잘 자라고 따뜻한 온도와 촉촉한 습도를 좋아해 주변 공기를 촉촉하게 관리해주시면 누구나 건강하게 키울 수 있어요. 또한 독성이 없어 반려동물을 기르시는 집사님들도 걱정 없이 키울 수 있는 식물이랍니다!', '평균 주 1~2회
흙 표면부터 3cm까지 마르면, 듬뿍 주세요', '반음지', '70% 이상', '21~25℃', '13℃ 이상', '연녹색잎에 진녹색의 반점', '잎을 감상하는', false, '둥근모양', '무늬가 있는', '잎이 두꺼운', '잎이 빳빳한', NULL, '안전', '소, 중형', '중', '소: 4천원~6천원
중: 1만5천원~2만원', NULL, NULL, '2025-05-26 15:33:26.883516'),
(938, '제옥 (플레이오스필로스 넬리)', NULL, 'Pleiospilos nelii', '제옥은 마치 코뿔소를 연상시키는 특이한 모양의 다육식물입니다. 흙 가까이에서 뽈록 솟아오른 돌 같이 보이기도 해 서양에서는 ''Split Rock''이라고 불려요. 이러한 특이한 외형은 제옥이 살아남기 위해 특별하게 진화된 결과물이에요. 남아프리카의 건조한 사막지대에서 온 제옥은 주변 바위틈이나 자갈들 사이에서 성장하는데, 다른 동물들로부터 자신의 몸을 보호하기 위해 주변 색과 비슷한 색과 질감으로 자란다고 해요. 가을에 새 잎을 낼 때 제옥은 파충류처럼 ''탈피''를 해요. 오래된 잎이 벌어지면 그 사이에서 새 잎이 나오는 신기한 식물이랍니다.', '평균 월 1회 이하
통통한 잎이 살짝 쭈글해지면 듬뿍 주세요', '양지', '40% 이하', '18~27℃', '10℃ 이상', '색상다양', '다육/선인장', false, '둥근모양', '무늬가 있는', '잎이 두꺼운', NULL, '초보자', '안전', '소형', '하', '3천원~5천원', NULL, NULL, '2025-05-26 15:33:26.01895'),
(961, '칼라디움', NULL, 'Caladium spp.', '칼라디움은 여름을 대표하는 관엽식물이에요. 잎만으로도 꽃보다 아름다운 미모를 뽐내는 칼라디움은 전 세계 2,000종 이상의 다양한 품종이 있어요. 하트 모양의 크기가 다양한 잎과 크고 화려한 무늬를 가지고 있답니다. 남아메리카에서 온 칼라디움은 따뜻한 온도와 촉촉한 습도를 아주 좋아하는 식물입니다. 빛이 적은 곳에서도 잘 자라지만 아름다운 무늬와 잎의 성장을 위해서는 한 번 걸러진 밝은 빛이 오래 머무는 공간이 적합해요. 칼라디움은 알뿌리식물로, 밤온도가 20℃ 이하로 떨어지면 겨울잠을 잘 준비를 해요. 겨울잠을 위해 새 순 내는 속도가 느려지고 줄기에 힘이 없어지며 잎이 마르기 시작한답니다. 잎이 시들기 시작하면 줄기를 모두 잘라주시고, 뿌리(구근)을 캐서 모래나 톱밥에 묻어 약 15~20℃의 온도를 유지하며 겨울잠을 재워주셔야 한답니다. 완연한 봄이 오면(4월) 보관했던 구근을 다시 심어주시고 따뜻한 햇살을 맞게 해주시면 새 순이 쏙 나온답니다. 키우던 화분 그대로 겨울을 나도 이듬해 봄이 오면 새싹을 내어주는 기특한 식물이랍니다. 겨울잠을 재우지 않고 계속 키워주셔도 되지만, 더 풍성한 잎과 예쁜 무늬를 위해서는 겨울잠을 재워주는 게 좋아요.

평균 주 1~2회
